import Form from 'react-bootstrap/Form';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './form.module.css';
import { Formik } from 'formik';


const BasicFormik = () => (
    <div>
      <h1>Anywhere in your app!</h1>
      <Formik
        initialValues={{ email: '', password: '' }}
        validate={values => {
          const errors = {};
          if (!values.email) {
            errors.email = 'Required';
          } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
          ) {
            errors.email = 'Invalid email address';
          }
          return errors;
        }}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
          }, 400);
        }}
      >
        {({
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting,
          /* and other goodies */
        }) => (
    <FormAdvertisement/>
        )}
      </Formik>
    </div>
  );
  
  export default BasicFormik;
  export const FormAdvertisement=()=> {
    return (
      <div className={classes.gridcontainerelement}>
      <div className={classes.gridchildelement }>
      <Form.Label>Type of vehicle</Form.Label>
        <Form.Select size="lg">
          <option>Cars</option>
          </Form.Select>
          <Form.Label>Brand</Form.Label>
          <Form.Select size="lg">
          <option>BMW</option>
          </Form.Select>
          <Form.Label>model</Form.Label>
          <Form.Select size="lg">
          <option>X5</option>
          </Form.Select>
          <Form.Check
            inline
            label="Verified VIN"
            name="group1"
          />          </div>
<div className={classes.childForm}>
<Form.Label>Region</Form.Label>
        <Form.Select size="lg">
          <option>Kyiv</option>
          </Form.Select>
          <Form.Label>Year</Form.Label>
          <Form.Select size="lg">
          <option>1990</option>
          </Form.Select>
          <Form.Select size="lg">
          <option>2090</option>
          </Form.Select>
          <Form.Label>Price $</Form.Label>
          <Form.Select size="lg">
          <option>1</option>
          </Form.Select>
          <Form.Select size="lg">
          <option>100000</option>
          </Form.Select>
</div>

      </div>
    );
  };
